import logo from "./logo.svg";
import React from "react";
import { useRef } from "react";
import "./App.css";

const shape = () => {
  <div
    className="shape"
    ref={shape}
    style={{
      width: "300px",
      height: "300px",
      borderRadius: "50%",
      backgroundColor: "yellow",
      margin: "25px",
      display: "block",
    }}
    onClick={onClick}
  ></div>;
};

function App() {
  const shape = useRef(null);
  const onClick = () => {
    shape.current.style.backgroundColor = "blue";
  };
  return (
    <div className="App">
      <h1>Change the colour of a shape by clicking on it.</h1>
      <Shape
        style={{
          width: "300px",
          height: "300px",
          borderRadius: "50%",
          backgroundColor: "yellow",
          margin: "25px",
          display: "block",
        }}
        onClick={onClick}
      ></Shape>
      <Shape
        className="shape"
        ref={shape}
        style={{
          width: "300px",
          height: "300px",
          borderRadius: "20%",
          backgroundColor: "red",
          margin: "25px",
          display: "block",
        }}
        onClick={onClick}
      ></Shape>
    </div>
  );
}

export default App;
