import logo from './logo.svg';
import React from 'react';
import { useRef } from 'react';
import './App.css';

function App() {
  const shape = useRef(0);
  const onClick = () => {
    shape.current.backgroundColor = "blue";
  };
  return (
    <div className="App">
      <div className="shape" ref={shape} style={{ width: "300px", height: "300px", borderRadius: "50%", backgroundColor: "yellow", margin: "25px", display: "block"}}></div>
      <button onClick={onClick} style={{display: "block", margin: "25px"}}>Change colour of the shape</button>
    </div>
  );
}

export default App;
