import logo from './logo.svg';
import './App.css';

function App() {
  const shape = useRef(0);
  const onClick = () => {
    shape.current.backgroundColor = "blue";
  };
  return (
    <div className="App">
      <div className="shape"></div>
      <button onClick={onClick}>Change colour of the shape</button>
    </div>
  );
}

export default App;
